package eth_test

import (
	"encoding/json"
	"testing"

	"github.com/stretchr/testify/require"

	"github.com/INFURA/go-ethlibs/eth"
)

func TestTransactionFailedContractCreation(t *testing.T) {
	// This is a transaction that tries to create a contract but failed (creates is null)
	payload := `{"blockHash":"0x774c34a19ff36b1d3669c54fe385502ad32862ed728da53b72b771f82c08b474","blockNumber":"0x6e2e24","from":"0x6b59210ade46b62b25e82e95ab390a7ccadd4c3a","gas":"0x2dc6c0","gasPrice":"0x1dcd65000","hash":"0xa6c1da26576bc41a722a3058bb1fa36e9c58188bd960bc1f40a976b6cb25e15e","input":"","nonce":"0x5f1f","r":"0xbe9872010301ebd71ea659b54b5d8b9f117249c9a5c23ef65de21c7807c7ddc8","s":"0x883971fb706e2336928a21be320c98c457238c2ef3466ba437f2f0bbfe32ad7","to":null,"transactionIndex":"0x6","v":"0x26","value":"0x0"}`

	tx := eth.Transaction{}
	err := json.Unmarshal([]byte(payload), &tx)
	require.NoError(t, err)
	require.Nil(t, tx.To)

	j, err := json.Marshal(&tx)
	require.NoError(t, err)

	RequireEqualJSON(t, []byte(payload), j)
}

func TestTransactionSuccessfulContractCreation(t *testing.T) {
	payload := `{"blockHash":"0x00e6ace83980aa65ccde1ad2887671ca99d7a83d490204d3f8efc4d194bf8bcd","blockNumber":"0x9e346f","chainId":null,"condition":null,"creates":"0xac2475e9325b586f0b7e1928b813c9098d9ba262","from":"0x2cf222b75ffbf44cac4c5ac706dc0a756dfce9cd","gas":"0x124f80","gasPrice":"0x4a817c800","hash":"0x0cef0f5a1e7258d464646f919daaec612d27b7c2a25b11f26a090525a2feeac3","input":"","nonce":"0xdf","publicKey":"0x48f6c6c63206c73980e90a0f5f39cfd60352606ec0347819a6dc30cf85853588cff72945bb14e22234d0ca470e705cb033849910ec65e6c03f61ca88120e1cf0","r":"0x889510df0e466a858d0ef38e7ce4b95f3fdd43b02ca771d07cb8976a1de15679","raw":"","s":"0x106250ebdf433dff9bef988555a6c6a0f12193b52aef63c0af8cc1b39e387cd","standardV":"0x0","to":null,"transactionIndex":"0x0","v":"0x1b","value":"0x0"}`

	tx := eth.Transaction{}
	err := json.Unmarshal([]byte(payload), &tx)
	require.NoError(t, err)
	require.Nil(t, tx.To)
	require.Equal(t, eth.MustAddress("0xac2475e9325b586f0b7e1928b813c9098d9ba262"), tx.Creates)

	j, err := json.Marshal(&tx)
	require.NoError(t, err)

	RequireEqualJSON(t, []byte(payload), j)
}

func TestNewPendingTxNotificationParams(t *testing.T) {

	{
		params := eth.NewPendingTxNotificationParams{}

		pTrx := `{"subscription":"0x70b8a3979a9df0ae3c2e006a192bbd27","result":"0xa2d258a5f2b0ff053a7bb7daa9fba98834dbb5aa6815dbad1c5c7837bbb436a3"}`

		err := json.Unmarshal([]byte(pTrx), &params)
		require.NoError(t, err)
		require.Equal(t, *eth.MustHash("0xa2d258a5f2b0ff053a7bb7daa9fba98834dbb5aa6815dbad1c5c7837bbb436a3"), params.Result)

		j, err := json.Marshal(&params)
		require.NoError(t, err)

		RequireEqualJSON(t, []byte(pTrx), j)
	}

	{
		params := eth.NewPendingTxNotificationParams{}

		parity := `{"result":"0x1c40d1cface680124774279a5806014c9d1768e46b5520e5ab1f6239b9ee2e76","subscription":"0x5beac702152c0d1f"}`

		err := json.Unmarshal([]byte(parity), &params)
		require.NoError(t, err)
		require.Equal(t, *eth.MustHash("0x1c40d1cface680124774279a5806014c9d1768e46b5520e5ab1f6239b9ee2e76"), params.Result)

		j, err := json.Marshal(&params)
		require.NoError(t, err)

		RequireEqualJSON(t, []byte(parity), j)
	}

	{
		params := eth.NewPendingTxNotificationParams{}

		kovan := `{"result":"0xc1baf4fd2b6a4f26f53ee6da7b82a69e055ea97b03a1b023afe028643dc12dc0","subscription":"0x5beac702152c0d1f"}`

		err := json.Unmarshal([]byte(kovan), &params)
		require.NoError(t, err)
		require.Equal(t, *eth.MustHash("0xc1baf4fd2b6a4f26f53ee6da7b82a69e055ea97b03a1b023afe028643dc12dc0"), params.Result)

		j, err := json.Marshal(&params)
		require.NoError(t, err)

		RequireEqualJSON(t, []byte(kovan), j)
	}
}

func TestNewTransaction(t *testing.T) {
	data := []byte("0x")
	tx, err := eth.NewTransaction(5, 21488430592, 90000, "0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed", 1, data)
	require.NoError(t, err)
	require.Equal(t, tx.Nonce, *eth.MustQuantity("0x5"))
	require.Equal(t, tx.GasPrice, eth.QuantityFromInt64(21488430592))
	require.Equal(t, tx.Gas, eth.QuantityFromInt64(90000))
	require.Equal(t, tx.To.String(), "0x5aAeb6053F3E94C9b9A09f33669435E7Ef1BeAed")
	require.Equal(t, tx.Value, eth.QuantityFromInt64(1))
}